import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item-edit',
  templateUrl: './item-edit.component.html',
  styleUrls: ['./item-edit.component.css'],
})
export class ItemEditComponent implements OnInit {
  itemId!: number;
  editedItem: any;

  constructor(private route: ActivatedRoute, private itemService: ItemService) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.itemId = +params['id'];
      this.loadItem();
    });
  }

  loadItem(): void {
    // Llama al servicio para obtener los detalles del ítem
    this.itemService.getItem(this.itemId).subscribe((data) => {
      this.editedItem = data;
      console.log('Item loaded:', data);
    });
  }

  editItem(): void {
    // Llama al servicio para editar un ítem
    this.itemService.editItem(this.itemId, this.editedItem).subscribe(() => {
      // Realiza acciones adicionales después de editar
      console.log('Item edited');
    });
  }
}
<!-- item-edit.component.html -->
<h2>Editar Ítem</h2>
<form *ngIf="editedItem">
  <!-- Agrega campos y etiquetas según tus atributos -->
  <label for="labId">LAB_ID:</label>
  <input type="text" id="labId" [(ngModel)]="editedItem.Lab_Id" name="labId" required>

  <label for="ievaActo">IEVA_ACTO:</label>
  <input type="text" id="ievaActo" [(ngModel)]="editedItem.Ieva_Acto" name="ievaActo" required>

  <!-- Agrega más campos según tus atributos -->

  <button type="button" (click)="editItem()">Guardar Cambios</button>
</form>
