  // login.component.ts
  import { Component } from '@angular/core';
  import { Router } from '@angular/router';
  import { AuthService } from '../auth.service';

  @Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css'],
  })
  export class LoginComponent {
    email: string = '';
    password: string = '';
    loginError: string = '';

    constructor(private authService: AuthService, private router: Router) { }

    login(): void {
      this.authService.login(this.email, this.password).subscribe(
        (response) => {
          // Login exitoso
          console.log('Login exitoso', response);
          console.log('Token recibido:', response.token);
          localStorage.setItem('token', response.token); // Almacena el token en localStorage
          this.router.navigate(['/items/add']); // Redirige a la página después del inicio de sesión
        },
        (error) => {
          // Error en el inicio de sesión
          console.error('Error en el inicio de sesión', error);

          if (error.status === 401) {
            this.loginError = 'Credenciales incorrectas';
          } else {
            this.loginError = 'Error en el servidor al intentar iniciar sesión';
          }
        }
      );
    }
  }
<!-- login.component.html -->
<div class="container">
  <div class="screen">
    <div class="screen__content">
      <img class="university-logo" src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/57/Logo_de_la_Universidad_del_Cauca.svg/2048px-Logo_de_la_Universidad_del_Cauca.svg.png" alt="University Logo">
      <form class="login">
        <div class="login__field">
          <img src="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/svgs/solid/user.svg" class="login__icon" alt="User Icon">
          <input type="text" class="login__input" placeholder="Email" [(ngModel)]="email" name="email">
        </div>
        <div class="login__field">
          <img src="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/svgs/solid/lock.svg" class="login__icon" alt="Lock Icon">
          <input type="password" class="login__input" placeholder="Contraseña" [(ngModel)]="password" name="password">
        </div>
        <button class="button login__submit" (click)="login()">
          <span class="button__text">Iniciar sesión</span>
          <img src="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/svgs/solid/chevron-right.svg" class="button__icon" alt="Right Chevron Icon">
        </button>
      </form>
      <div *ngIf="loginError" class="error-message">{{ loginError }}</div>
    </div>
    <div class="screen__background">
      <span class="screen__background__shape screen__background__shape4"></span>
      <span class="screen__background__shape screen__background__shape3"></span>
      <span class="screen__background__shape screen__background__shape2"></span>
      <span class="screen__background__shape screen__background__shape1"></span>
    </div>
  </div>
</div>
