import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, catchError, throwError } from 'rxjs';

const API_URL = 'http://localhost:3000/items';

@Injectable({
  providedIn: 'root',
})
export class ItemService {
  constructor(private http: HttpClient) { }

  getItems(): Observable<any> {
    return this.http.get(API_URL);
  }

  getItemsByEvaluation(evaId: number): Observable<any> {
    console.log(`Solicitud para obtener items de la evaluación ID ${evaId}`);
    return this.http.get(`${API_URL}/${evaId}`);
  }

  getItem(id: number): Observable<any> {
    return this.http.get(`${API_URL}/detalle/${id}`);
  }

  addItem(item: any): Observable<any> {
    const token = localStorage.getItem('token'); // Obtén el token del almacenamiento local

    // Verificar token
    if (!token) {
      // Manejo de error si el token no está presente
      return throwError('Token not provided!');
    } else {
      
      // Incluye el token en las cabeceras
      const headers = new HttpHeaders({ Authorization: token });
      return this.http.post(`${API_URL}/add`, item, { headers }).pipe(
        catchError((error: any) => {
          return throwError('Error in token');
        })
      );
    }
  }

  editItem(id: number, item: any): Observable<any> {
    return this.http.put(`${API_URL}/editar/${id}`, item);
  }

  deleteItem(id: number): Observable<any> {
    return this.http.delete(`${API_URL}/eliminar/${id}`);
  }
}
