// item-add.component.ts
import { Component } from '@angular/core';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item-add',
  templateUrl: './item-add.component.html',
  styleUrls: ['./item-add.component.css'],
})
export class ItemAddComponent {
  newItem: any = {
    lab_id: null,
    eva_id: 1, // Aquí puedes establecer el ID de evaluación deseado
    ieva_acto: null,
    ieva_estado: null,
    ieva_puntaje: null,
  };

  constructor(private itemService: ItemService) { }

  addItem(): void {
    // Llama al servicio para agregar un ítem
    this.itemService.addItem(this.newItem).subscribe(() => {
      // Realiza acciones adicionales después de agregar
      console.log('Item added');
    });
  }
}
<!-- item-add.component.html -->
<h2>Agregar Ítem</h2>
<form>
  <!-- Agrega campos y etiquetas según tus atributos -->
  <label for="labId">LAB_ID:</label>
  <input type="text" id="labId" [(ngModel)]="newItem.lab_id" name="labId" required>

  <label for="ievaActo">IEVA_ACTO:</label>
  <input type="text" id="ievaActo" [(ngModel)]="newItem.ieva_acto" name="ievaActo" required>

  <!-- Agrega más campos según tus atributos -->

  <button type="button" (click)="addItem()">Agregar</button>
</form>
