<div class="container">
  <h2>Lista de Items</h2>

  <div *ngIf="items.length > 0; else noItems">
    <table class="table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Lab ID</th>
          <th>Evaluación ID</th>
          <th>Acto</th>
          <th>Estado</th>
          <th>Puntaje</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of items">
          <td>{{ item.atrIevaId }}</td>
          <td>{{ item.atrLabId }}</td>
          <td>{{ item.atrEvaId }}</td>
          <td>{{ item.atrIevaActo ? 'Sí' : 'No' }}</td>
          <td>{{ item.atrIevaEstado }}</td>
          <td>{{ item.atrIevaPuntaje }}</td>
        </tr>
      </tbody>
    </table>
  </div>

  <ng-template #noItems>
    <p>No hay items disponibles.</p>
  </ng-template>
</div>
