import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item-edit',
  templateUrl: './item-edit.component.html',
  styleUrls: ['./item-edit.component.css'],
})
export class ItemEditComponent implements OnInit {
  itemId!: number;
  editedItem: any;

  constructor(private route: ActivatedRoute, private itemService: ItemService) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.itemId = +params['id'];
      this.loadItem();
    });
  }

  loadItem(): void {
    // Llama al servicio para obtener los detalles del ítem
    this.itemService.getItem(this.itemId).subscribe((data) => {
      this.editedItem = data;
      console.log('Item loaded:', data);
    });
  }

  editItem(): void {
    // Llama al servicio para editar un ítem
    this.itemService.editItem(this.itemId, this.editedItem).subscribe(() => {
      // Realiza acciones adicionales después de editar
      console.log('Item edited');
    });
  }
}
<!-- item-edit.component.html -->
<h2>Editar Ítem</h2>
<form *ngIf="editedItem">
  <!-- LAB_ID -->
  <label for="Lab_Id">LAB_ID:</label>
  <input type="text" id="Lab_Id" [(ngModel)]="editedItem.LAB_ID" name="Lab_Id" required>

  <!-- EVA_ID -->
  <label for="Eva_Id">EVA_ID:</label>
  <input type="text" id="Eva_Id" [(ngModel)]="editedItem.EVA_ID" name="Eva_Id" required>

  <!-- IEVA_ACTO -->
  <label for="Ieva_Acto">IEVA_ACTO:</label>
  <input type="text" id="Ieva_Acto" [(ngModel)]="editedItem.IEVA_ACTO" name="Ieva_Acto" required>

  <!-- IEVA_ESTADO -->
  <label for="Ieva_Estado">IEVA_ESTADO:</label>
  <input type="text" id="Ieva_Estado" [(ngModel)]="editedItem.IEVA_ESTADO" name="Ieva_Estado" required>

  <!-- IEVA_PUNTAJE -->
  <label for="Ieva_Puntaje">IEVA_PUNTAJE:</label>
  <input type="text" id="Ieva_Puntaje" [(ngModel)]="editedItem.IEVA_PUNTAJE" name="Ieva_Puntaje" required>

  <button type="button" (click)="editItem()">Guardar Cambios</button>
</form>
