// item-remove.component.ts
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item-remove',
  templateUrl: './item-remove.component.html',
  styleUrls: ['./item-remove.component.css'],
})
export class ItemRemoveComponent implements OnInit {
  itemId!: number;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private itemService: ItemService
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.itemId = +params['id'];
      this.removeItem();
    });
  }

  removeItem(): void {
    this.itemService.deleteItem(this.itemId).subscribe(() => {
      console.log('Item removed');
      this.router.navigate(['/items']);
    });
  }
}
