  // login.component.ts
  import { Component } from '@angular/core';
  import { Router } from '@angular/router';
  import { AuthService } from '../auth.service';

  @Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css'],
  })
  export class LoginComponent {
    email: string = '';
    password: string = '';
    loginError: string = '';

    constructor(private authService: AuthService, private router: Router) { }

    login(): void {
      this.authService.login(this.email, this.password).subscribe(
        (response) => {
          // Login exitoso
          console.log('Login exitoso', response);
          console.log('Token recibido:', response.token);
          localStorage.setItem('token', response.token); // Almacena el token en localStorage
          this.router.navigate(['/items/add']); // Redirige a la página después del inicio de sesión
        },
        (error) => {
          // Error en el inicio de sesión
          console.error('Error en el inicio de sesión', error);

          if (error.status === 401) {
            this.loginError = 'Credenciales incorrectas';
          } else {
            this.loginError = 'Error en el servidor al intentar iniciar sesión';
          }
        }
      );
    }
  }
<!-- login-form.component.html -->
<div class="screen-1">
  <svg class="logo" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" version="1.1" width="300" height="300" viewbox="0 0 640 480" xml:space="preserve">
    <!-- SVG content -->
  </svg>
  <div class="email">
    <label for="email">Email Address</label>
    <div class="sec-2">
      <ion-icon name="mail-outline"></ion-icon>
      <input type="email" id="email" [(ngModel)]="email" placeholder="Username@gmail.com" />
    </div>
  </div>
  <div class="password">
    <label for="password">Password</label>
    <div class="sec-2">
      <ion-icon name="lock-closed-outline"></ion-icon>
      <input class="pas" type="password" id="password" [(ngModel)]="password" placeholder="············" />
      <ion-icon class="show-hide" name="eye-outline"></ion-icon>
    </div>
  </div>
  <button class="login" (click)="login()">Login</button>
  <div class="footer"><span>Signup</span><span>Forgot Password?</span></div>
  <div *ngIf="loginError" style="color: red;">{{ loginError }}</div>
</div>
