// item-remove.component.ts
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-item-remove',
  templateUrl: './item-remove.component.html',
  styleUrls: ['./item-remove.component.css'],
})
export class ItemRemoveComponent implements OnInit {
  itemId!: number;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private itemService: ItemService
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.itemId = +params['id'];
      this.removeItem();
    });
  }

  removeItem(): void {
    this.itemService.deleteItem(this.itemId).subscribe(() => {
      console.log('Item removed');
      this.router.navigate(['/items']);
    });
  }
}
<div class="container">
  <h2>Lista de Items</h2>

  <div *ngIf="items.length > 0; else noItems">
    <table class="table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Lab ID</th>
          <th>Evaluación ID</th>
          <th>Acto</th>
          <th>Estado</th>
          <th>Puntaje</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of items">
          <td>{{ item.atrIevaId }}</td>
          <td>{{ item.atrLabId }}</td>
          <td>{{ item.atrEvaId }}</td>
          <td>{{ item.atrIevaActo ? 'Sí' : 'No' }}</td>
          <td>{{ item.atrIevaEstado }}</td>
          <td>{{ item.atrIevaPuntaje }}</td>
        </tr>
      </tbody>
    </table>
  </div>

  <ng-template #noItems>
    <p>No hay items disponibles.</p>
  </ng-template>
</div>
